CREATE PROCEDURE [dbo].[sp_MergeBalanceDatasets]
    @CYDate NVARCHAR(10),
    @PYDate NVARCHAR(10)
AS
BEGIN
    SET NOCOUNT ON;

    -- Drop if exists
    IF OBJECT_ID('[dbo].[Merged_Balance]', 'U') IS NOT NULL
        DROP TABLE [dbo].[Merged_Balance];

    -- Create merged dataset with source year info
    SELECT
        @CYDate AS [Year],
        [acc_id],
        [product],
        [balance],
        [interest]
    INTO [dbo].[Merged_Balance]
    FROM [dbo].[Balance_'+@CYDate+']

    UNION ALL

    SELECT
        @PYDate AS [Year],
        [acc_id],
        [product],
        [balance],
        [interest]
    FROM [dbo].[Balance_'+@PYDate+'];

    PRINT 'Datasets merged successfully.';
END;
