CREATE PROCEDURE [dbo].[sp_CheckForAnomalies]
AS
BEGIN
    SET NOCOUNT ON;

    -- Check for negative balances
    SELECT * FROM [dbo].[Merged_Balance]
    WHERE CAST([balance] AS FLOAT) < 0;

    -- Check for missing account IDs
    SELECT * FROM [dbo].[Merged_Balance]
    WHERE [acc_id] IS NULL OR LTRIM(RTRIM([acc_id])) = '';


    PRINT 'Anomaly checks completed.';
END;
